import { Component,OnInit} from '@angular/core';
import { FormGroup,FormBuilder } from '@angular/forms';
import { Allowance } from '../models/allowance.model';

@Component({
  selector: 'app-add-class',
  templateUrl: './add-class.component.html',
  styleUrls: ['./add-class.component.css']
})
export class AddClassComponent implements OnInit {

  allowanceLoginForm!:FormGroup;
  allowance!:Allowance;
  errMessage!:string;
  constructor(private fb:FormBuilder,private loginService:admi ) { }

  ngOnInit(): void {

  }

  createForm(){
    this.allowanceLoginForm = this.fb.group({
      Email : "",
      TypeId :"",
      ClassName:"",
      BasicSalary:"",
      HRAllowance:"",
      DAAllowance:"",
      TravelAllowance:"",
      MedicalAllowance:"",
      WashingAllowance:"",
      LeaveDeduction:""
    });
  }
  async LoginUser(){
    this.login = this.loginForm.value;
    console.log(this.login);
    (await this.loginService.sendLoginUser(this.login))
    .subscribe(
      login => {this.login = login,console.log(login)} ,
      errMess => { this.login = <any> null;this.errMess = <any>errMess;}
    )
    this.loginForm.reset();
  }

}
