import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-add-admins',
  templateUrl: './add-admins.component.html',
  styleUrls: ['./add-admins.component.css']
})
export class AddAdminsComponent {

  addAdminForm!: FormGroup;
  addEmployee!: AddEmployee;
  errMessage!: string;

  constructor(private fb: FormBuilder, private adminService: AdminService, private router: Router) { }

  ngOnInit(): void {
    this.createForm();
  }

  createForm() {
    this.addEmployeeForm = this.fb.group({
      FirstName: "",
      LastName: "",
      Email: "",
      Address: "",
      Password: "",
      PhoneNumber: "",
      JoiningDate: "",
      Position: ""
    });
  }
  async AddEmployee() {
    this.addEmployee = this.addEmployeeForm.value;
    console.log(this.addEmployee);
    (await this.adminService.AddEmployee(this.addEmployee))
      .subscribe(
        addEmployee => { this.addEmployee = addEmployee, console.log(addEmployee) },
        errMessage => { this.addEmployee = <any>null; this.errMessage = <any>errMessage; }
      )
    this.addEmployeeForm.reset();
  }

  goToManageAdmins() {
    this.router.navigate(['/admin-dashboard/all-admins']);
  }

}
