import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserViewModel } from 'src/app/models/user.model';
import { AdminService } from 'src/app/services/admin.service';

@Component({
  selector: 'app-all-employees',
  templateUrl: './all-employees.component.html',
  styleUrls: ['./all-employees.component.css']
})
export class AllEmployeesComponent implements OnInit {

  [10:31] Bahunadham, Sai Ajith Naveen Babu

employees!: UserViewModel[];



  constructor(private router: Router, private adminService: AdminService) { }

  async ngOnInit(): Promise<void> {

    const data = await (await this.adminService.GetEmployee()).toPromise();

    if (data) {

      this.employees = data;

      console.log(this.employees);

    } else {

      this.employees = [];

    }
  }

  addEmployee() {
    this.router.navigate(['/admin-dashboard/add-employee']);
  }
  updateEmployee() {
    this.router.navigate(['/admin-dashboard/update-employee'])
  }
  addClass() {
    this.router.navigate(['/admin-dashboard/add-class'])
  }}
<body>
    <div class="container">
        <div class="table-wrapper">
            <div class="table-title">
                <div class="row">
                    <div class="col-sm-12">
						<h2>Manage <b>Employees</b></h2>
					</div>
					<div class="container">	
                        <button class="classbutton" (click)="addClass()"><span>Add Class</span></button>	
                        <button class="button" (click)="addEmployee()"><span>Add Employee</span></button>					
					</div>
                </div>
            </div>
            <table class="table table-striped table-hover">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Address</th>
                        <th>Phone</th>
                        <th>Position</th>
                        <th>Date of join</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
					<tr *ngFor="let employee of employees">
                        <td>{{employee.fullName}}</td>
                        <td>{{employee.email}}</td>
                        <td>{{employee.address}}</td>
                        <td>{{employee.phoneNumber}}</td>
                        <td>{{employee.position}}</td>
                        <td>{{employee.joiningDate | date}}</td>
                        <td>
                            <div class="button-container">
                                <button class="update" (click)="updateEmployee()">Update</button>
                                <button class="delete">Delete</button>
                              </div> 
                        </td>
                    </tr>

                </tbody>
            </table>
        </div>
    </div>
</body>
